contracts/AutoExecution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "./EnhancedProposal.sol";
import "./Vault.sol";
import "./GovernanceToken.sol";

/**
 * @title AutoExecution
 * @dev 자동화된 제안 실행 시스템과 보안 강화
 *      - 투표 종료 후 자동 실행
 *      - 스케줄링된 실행
 *      - 멀티시그 보안
 *      - 재진입 공격 방지
 */

contract AutoExecution is AccessControl, ReentrancyGuard {
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    EnhancedProposal public proposalContract;
    Vault public vaultContract;
    GovernanceToken public governanceToken;

    // 실행 대기열
    struct ExecutionQueue {
        uint256 proposalId;
        uint256 scheduledTime;
        bool isExecuted;
        address executor;
    }

    // 멀티시그 설정
    struct MultiSigConfig {
        uint256 requiredSignatures;
        uint256 executionDelay;
        mapping(address => bool) signers;
    }

    mapping(uint256 => ExecutionQueue) public executionQueue;
    mapping(uint256 => mapping(address => bool)) public proposalSignatures;
    mapping(uint256 => uint256) public proposalSignatureCount;

    MultiSigConfig public multiSigConfig;

    uint256 public queueCount;
    uint256 public constant MIN_EXECUTION_DELAY = 1 hours;
    uint256 public constant MAX_EXECUTION_DELAY = 7 days;

    // 이벤트
    event ExecutionScheduled(uint256 indexed proposalId, uint256 scheduledTime);
    event ExecutionExecuted(uint256 indexed proposalId, address indexed executor);
    event ExecutionCancelled(uint256 indexed proposalId, address indexed canceller);
    event MultiSigSignatureAdded(uint256 indexed proposalId, address indexed signer);
    event MultiSigConfigUpdated(uint256 requiredSignatures, uint256 executionDelay);

    constructor(
        address _proposalContract,
        address payable _vaultContract,
        address _governanceToken,
        address admin
    ) {
        proposalContract = EnhancedProposal(_proposalContract);
        vaultContract = Vault(_vaultContract);
        governanceToken = GovernanceToken(_governanceToken);

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(EXECUTOR_ROLE, admin);
        _grantRole(EMERGENCY_ROLE, admin);

        // 멀티시그 초기 설정
        multiSigConfig.requiredSignatures = 2;
        multiSigConfig.executionDelay = 24 hours;
        multiSigConfig.signers[admin] = true;
    }

    // 멀티시그 서명자 추가
    function addMultiSigSigner(address signer) external onlyRole(DEFAULT_ADMIN_ROLE) {
        multiSigConfig.signers[signer] = true;
    }

    // 멀티시그 서명자 제거
    function removeMultiSigSigner(address signer) external onlyRole(DEFAULT_ADMIN_ROLE) {
        multiSigConfig.signers[signer] = false;
    }

    // 멀티시그 설정 업데이트
    function updateMultiSigConfig(uint256 requiredSignatures, uint256 executionDelay) 
        external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(requiredSignatures > 0, "AutoExecution: invalid required signatures");
        require(executionDelay >= MIN_EXECUTION_DELAY, "AutoExecution: delay too short");
        require(executionDelay <= MAX_EXECUTION_DELAY, "AutoExecution: delay too long");

        multiSigConfig.requiredSignatures = requiredSignatures;
        multiSigConfig.executionDelay = executionDelay;

        emit MultiSigConfigUpdated(requiredSignatures, executionDelay);
    }

    // 제안 실행 스케줄링
    function scheduleExecution(uint256 proposalId, uint256 delay) 
        external onlyRole(EXECUTOR_ROLE) nonReentrant {

        EnhancedProposal.ProposalData memory proposal = proposalContract.getProposal(proposalId);
        require(proposal.status == EnhancedProposal.Status.Passed, "AutoExecution: proposal not passed");
        require(block.timestamp >= proposal.endTime, "AutoExecution: voting not ended");
        require(delay >= MIN_EXECUTION_DELAY, "AutoExecution: delay too short");
        require(delay <= MAX_EXECUTION_DELAY, "AutoExecution: delay too long");

        uint256 scheduledTime = block.timestamp + delay;

        executionQueue[queueCount] = ExecutionQueue({
            proposalId: proposalId,
            scheduledTime: scheduledTime,
            isExecuted: false,
            executor: msg.sender
        });

        emit ExecutionScheduled(proposalId, scheduledTime);
        queueCount++;
    }

    // 긴급 실행 (긴급 제안용)
    function emergencyExecute(uint256 proposalId) 
        external onlyRole(EMERGENCY_ROLE) nonReentrant {

        EnhancedProposal.ProposalData memory proposal = proposalContract.getProposal(proposalId);
        require(proposal.isEmergency, "AutoExecution: not emergency proposal");
        require(proposal.status == EnhancedProposal.Status.Passed, "AutoExecution: proposal not passed");

        _executeProposal(proposalId);
    }

    // 멀티시그 서명 추가
    function addSignature(uint256 proposalId) external {
        require(multiSigConfig.signers[msg.sender], "AutoExecution: not authorized signer");
        require(!proposalSignatures[proposalId][msg.sender], "AutoExecution: already signed");

        proposalSignatures[proposalId][msg.sender] = true;
        proposalSignatureCount[proposalId]++;

        emit MultiSigSignatureAdded(proposalId, msg.sender);

        // 충분한 서명이 있으면 실행
        if (proposalSignatureCount[proposalId] >= multiSigConfig.requiredSignatures) {
            _executeProposal(proposalId);
        }
    }

    // 실행 가능한 제안 실행
    function executeScheduledProposals() external nonReentrant {
        for (uint256 i = 0; i < queueCount; i++) {
            ExecutionQueue storage queue = executionQueue[i];

            if (!queue.isExecuted && block.timestamp >= queue.scheduledTime) {
                _executeProposal(queue.proposalId);
                queue.isExecuted = true;
            }
        }
    }

    // 제안 실행 (내부 함수)
    function _executeProposal(uint256 proposalId) internal {
        EnhancedProposal.ProposalData memory proposal = proposalContract.getProposal(proposalId);

        // 실행 조건 확인
        require(proposal.status == EnhancedProposal.Status.Passed, "AutoExecution: proposal not passed");
        require(proposal.votesFor > proposal.votesAgainst, "AutoExecution: proposal not approved");

        // 정족수 확인
        uint256 totalVotes = proposal.votesFor + proposal.votesAgainst;
        uint256 approvalRate = (proposal.votesFor * 100) / totalVotes;

        EnhancedProposal.CategoryConfig memory config = proposalContract.getCategoryConfig(proposal.category);
        require(approvalRate >= config.quorumPercent, "AutoExecution: quorum not reached");

        // 금고에서 이체
        try vaultContract.transfer(proposal.recipient, proposal.amount) {
            // 제안 상태를 Executed로 변경
            proposalContract.setProposalStatus(proposalId, EnhancedProposal.Status.Executed);

            emit ExecutionExecuted(proposalId, msg.sender);
        } catch {
            revert("AutoExecution: transfer failed");
        }
    }

    // 실행 취소
    function cancelExecution(uint256 proposalId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < queueCount; i++) {
            if (executionQueue[i].proposalId == proposalId && !executionQueue[i].isExecuted) {
                executionQueue[i].isExecuted = true; // 취소 표시
                emit ExecutionCancelled(proposalId, msg.sender);
                break;
            }
        }
    }

    // 실행 대기열 조회
    function getExecutionQueue(uint256 index) external view returns (ExecutionQueue memory) {
        return executionQueue[index];
    }

    // 제안 서명 상태 조회
    function getProposalSignatures(uint256 proposalId) external view returns (uint256) {
        return proposalSignatureCount[proposalId];
    }

    // 서명자 확인
    function isSigner(address signer) external view returns (bool) {
        return multiSigConfig.signers[signer];
    }

    // 멀티시그 설정 조회
    function getMultiSigConfig() external view returns (uint256 requiredSignatures, uint256 executionDelay) {
        return (multiSigConfig.requiredSignatures, multiSigConfig.executionDelay);
    }

    // 실행 가능한 제안 확인
    function isExecutable(uint256 proposalId) external view returns (bool) {
        EnhancedProposal.ProposalData memory proposal = proposalContract.getProposal(proposalId);

        if (proposal.status != EnhancedProposal.Status.Passed) return false;
        if (proposal.votesFor <= proposal.votesAgainst) return false;

        uint256 totalVotes = proposal.votesFor + proposal.votesAgainst;
        uint256 approvalRate = (proposal.votesFor * 100) / totalVotes;

        EnhancedProposal.CategoryConfig memory config = proposalContract.getCategoryConfig(proposal.category);
        return approvalRate >= config.quorumPercent;
    }


}

contracts/CorruptionMonitor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title CorruptionMonitor
 * @dev DAO 부패지수 계산을 위한 모듈 (예시용)
 *      - 다양한 메트릭을 업데이트
 *      - 부패지수(Corruption Index) 계산 및 조회
 */
contract CorruptionMonitor {
  // 내부 매트릭 구조체 (metric: 어떤 시스템의 상태를 숫자로 측정한 값... 수치화된 관측 지표)
  struct Metrics{
    uint256 emergencyTransfers;      // 긴급 이체 발생 건수
    uint256 lowParticipation;        // 투표 참여율 저하 횟수
    uint256 dominantActions;         // 소수 지갑 독점 행동
    uint256 untrackedTransfers;      // 미등록 지출 건수
    uint256 gasWaste;                // 가스 낭비 건수
    uint256 proposalConcentration;   // 제안 집중도 지표
  }

  // Metrics 구조체 타입인 metrics 선언
  Metrics public metrics;

  //부패 지수 업데이트
  event CorruptionIndexUpdated(uint256 index);

  // Metrics 업데이트트
  function updateMetrics(
    uint256 emergency,
    uint256 lowPart,
    uint256 dominant,
    uint256 untracked,
    uint256 gas,
    uint256 concentration
  ) external{
      metrics = Metrics(emergency, lowPart, dominant, untracked, gas, concentration);

      emit CorruptionIndexUpdated(getCorruptionIndex());
  }

  //부패지수 계산 (일단 예시로 든 계산식임)
  function getCorruptionIndex() public view returns (uint256){
    uint256 raw = metrics.emergencyTransfers * 2
      + metrics.lowParticipation * 3
      + metrics.dominantActions * 2
      + metrics.untrackedTransfers * 4
      + metrics.gasWaste * 1
      + metrics.proposalConcentration * 3;
    return raw;
  }
}

contracts/EnhancedProposal.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./GovernanceToken.sol";

/**
 * @title EnhancedProposal
 * @dev 향상된 제안 시스템 - 카테고리별 제안, 긴급 제안, 제안 수정/취소
 *      - 카테고리별 다른 투표 기간과 정족수
 *      - 긴급 제안 시스템
 *      - 제안 수정/취소 기능
 */

contract EnhancedProposal is AccessControl {
    bytes32 public constant MEMBER_ROLE = keccak256("MEMBER_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    GovernanceToken public governanceToken;

    // 제안 카테고리
    enum Category { 
        GENERAL,        // 일반 제안
        FINANCIAL,      // 재무 제안
        OPERATIONAL,    // 운영 제안
        TECHNICAL,      // 기술 제안
        EMERGENCY       // 긴급 제안
    }

    // 제안 상태
    enum Status { 
        Pending, 
        Active, 
        Passed, 
        Rejected, 
        Executed,
        Cancelled,
        Modified
    }

    // 카테고리별 설정
    struct CategoryConfig {
        uint256 votingDuration;    // 투표 기간
        uint256 quorumPercent;     // 정족수 퍼센트
        uint256 minVotingPower;    // 최소 투표 파워
        bool isActive;             // 활성화 여부
    }

    // 제안 정보
    struct ProposalData {
        string title;
        string description;
        uint256 amount;
        address payable recipient;
        Category category;
        Status status;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 startTime;
        uint256 endTime;
        address proposer;
        uint256 version;           // 수정 버전
        bool isEmergency;
        uint256 emergencyThreshold; // 긴급 임계값
    }

    // 제안 수정 이력
    struct ProposalHistory {
        uint256 timestamp;
        string title;
        string description;
        uint256 amount;
        address payable recipient;
        uint256 version;
    }

    mapping(uint256 => ProposalData) public proposals;
    mapping(uint256 => mapping(uint256 => ProposalHistory)) public proposalHistory;
    mapping(Category => CategoryConfig) public categoryConfigs;

    uint256 public proposalCount;

    // 이벤트
    event ProposalCreated(uint256 indexed id, address indexed proposer, Category category);
    event ProposalModified(uint256 indexed id, address indexed modifierAddress, uint256 version);
    event ProposalCancelled(uint256 indexed id, address indexed canceller);
    event EmergencyProposalCreated(uint256 indexed id, address indexed proposer);
    event CategoryConfigUpdated(Category category, uint256 votingDuration, uint256 quorumPercent);

    constructor(address _governanceToken, address admin) {
        governanceToken = GovernanceToken(_governanceToken);
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MEMBER_ROLE, admin);
        _grantRole(EMERGENCY_ROLE, admin);

        // 카테고리별 기본 설정
        _setCategoryConfig(Category.GENERAL, 3 days, 50, 100 * 10**18);
        _setCategoryConfig(Category.FINANCIAL, 5 days, 60, 500 * 10**18);
        _setCategoryConfig(Category.OPERATIONAL, 3 days, 50, 100 * 10**18);
        _setCategoryConfig(Category.TECHNICAL, 7 days, 70, 1000 * 10**18);
        _setCategoryConfig(Category.EMERGENCY, 1 days, 40, 100 * 10**18);
    }

    // 카테고리 설정
    function _setCategoryConfig(
        Category category,
        uint256 votingDuration,
        uint256 quorumPercent,
        uint256 minVotingPower
    ) internal {
        categoryConfigs[category] = CategoryConfig({
            votingDuration: votingDuration,
            quorumPercent: quorumPercent,
            minVotingPower: minVotingPower,
            isActive: true
        });
    }

    // 카테고리 설정 업데이트 (관리자만)
    function updateCategoryConfig(
        Category category,
        uint256 votingDuration,
        uint256 quorumPercent,
        uint256 minVotingPower
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        categoryConfigs[category].votingDuration = votingDuration;
        categoryConfigs[category].quorumPercent = quorumPercent;
        categoryConfigs[category].minVotingPower = minVotingPower;

        emit CategoryConfigUpdated(category, votingDuration, quorumPercent);
    }

    // 제안 생성
    function createProposal(
        string calldata title,
        string calldata description,
        uint256 amount,
        address payable recipient,
        Category category
    ) external onlyRole(MEMBER_ROLE) returns (uint256) {
        require(categoryConfigs[category].isActive, "EnhancedProposal: category not active");

        uint256 votingPower = governanceToken.getVotingPower(msg.sender);
        require(votingPower >= categoryConfigs[category].minVotingPower, 
                "EnhancedProposal: insufficient voting power");

        uint256 proposalId = proposalCount++;
        uint256 votingDuration = categoryConfigs[category].votingDuration;

        proposals[proposalId] = ProposalData({
            title: title,
            description: description,
            amount: amount,
            recipient: recipient,
            category: category,
            status: Status.Pending,
            votesFor: 0,
            votesAgainst: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + votingDuration,
            proposer: msg.sender,
            version: 1,
            isEmergency: false,
            emergencyThreshold: 0
        });

        emit ProposalCreated(proposalId, msg.sender, category);
        return proposalId;
    }

    // 긴급 제안 생성
    function createEmergencyProposal(
        string calldata title,
        string calldata description,
        uint256 amount,
        address payable recipient,
        uint256 emergencyThreshold
    ) external onlyRole(EMERGENCY_ROLE) returns (uint256) {
        uint256 proposalId = proposalCount++;
        uint256 votingDuration = categoryConfigs[Category.EMERGENCY].votingDuration;

        proposals[proposalId] = ProposalData({
            title: title,
            description: description,
            amount: amount,
            recipient: recipient,
            category: Category.EMERGENCY,
            status: Status.Pending,
            votesFor: 0,
            votesAgainst: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + votingDuration,
            proposer: msg.sender,
            version: 1,
            isEmergency: true,
            emergencyThreshold: emergencyThreshold
        });

        emit EmergencyProposalCreated(proposalId, msg.sender);
        return proposalId;
    }

    // 제안 수정 (제안자만, 투표 시작 전)
    function modifyProposal(
        uint256 proposalId,
        string calldata title,
        string calldata description,
        uint256 amount,
        address payable recipient
    ) external {
        ProposalData storage proposal = proposals[proposalId];
        require(msg.sender == proposal.proposer, "EnhancedProposal: not proposer");
        require(proposal.status == Status.Pending, "EnhancedProposal: cannot modify active proposal");
        require(block.timestamp < proposal.endTime, "EnhancedProposal: voting already started");

        // 이력 저장
        proposalHistory[proposalId][proposal.version] = ProposalHistory({
            timestamp: block.timestamp,
            title: proposal.title,
            description: proposal.description,
            amount: proposal.amount,
            recipient: proposal.recipient,
            version: proposal.version
        });

        // 제안 수정
        proposal.title = title;
        proposal.description = description;
        proposal.amount = amount;
        proposal.recipient = recipient;
        proposal.version++;
        proposal.status = Status.Modified;

        emit ProposalModified(proposalId, msg.sender, proposal.version);
    }

    // 제안 취소 (제안자만)
    function cancelProposal(uint256 proposalId) external {
        ProposalData storage proposal = proposals[proposalId];
        require(msg.sender == proposal.proposer, "EnhancedProposal: not proposer");
        require(proposal.status == Status.Pending || proposal.status == Status.Modified, 
                "EnhancedProposal: cannot cancel active proposal");

        proposal.status = Status.Cancelled;
        emit ProposalCancelled(proposalId, msg.sender);
    }

    // 제안 활성화 (투표 시작)
    function activateProposal(uint256 proposalId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        ProposalData storage proposal = proposals[proposalId];
        require(proposal.status == Status.Pending || proposal.status == Status.Modified, 
                "EnhancedProposal: invalid status");

        proposal.status = Status.Active;
    }

    // 제안 상태 변경
    function setProposalStatus(uint256 proposalId, Status status) 
        external onlyRole(DEFAULT_ADMIN_ROLE) {
        proposals[proposalId].status = status;
    }

    // 제안 조회
    function getProposal(uint256 proposalId) external view returns (ProposalData memory) {
        return proposals[proposalId];
    }

    // 카테고리별 제안 목록 조회
    function getProposalsByCategory(Category category) external view returns (uint256[] memory) {
        uint256[] memory temp = new uint256[](proposalCount);
        uint256 count = 0;

        for (uint256 i = 0; i < proposalCount; i++) {
            if (proposals[i].category == category) {
                temp[count] = i;
                count++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = temp[i];
        }

        return result;
    }

    // 긴급 제안 목록 조회
    function getEmergencyProposals() external view returns (uint256[] memory) {
        uint256[] memory temp = new uint256[](proposalCount);
        uint256 count = 0;

        for (uint256 i = 0; i < proposalCount; i++) {
            if (proposals[i].isEmergency) {
                temp[count] = i;
                count++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = temp[i];
        }

        return result;
    }

    // 제안 이력 조회
    function getProposalHistory(uint256 proposalId, uint256 version) 
        external view returns (ProposalHistory memory) {
        return proposalHistory[proposalId][version];
    }

    // 카테고리 설정 조회
    function getCategoryConfig(Category category) external view returns (CategoryConfig memory) {
        return categoryConfigs[category];
    }


}

contracts/Execution.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Proposal.sol";
import "./Voting.sol";
import "./Vault.sol";

/**
 * @title Execution
 * @dev 투표가 통과된 제안을 실제로 집행하는 컨트랙트
 *      - 실행 조건 검사(isExecutable)
 *      - 금고에서 이체 후 제안 상태 변경
 */

contract Execution{
  // proposal, voting, vault 타입의 상태 변수 3개 생성성
  Proposal public proposalContract;
  Voting public votingContract;
  Vault public vaultContract;

  //정족수 퍼센트,,, 과반수 통과 기준을 퍼센트(%)로 선정
  uint256 public quorumPercent = 50;

  // 이벤트 : 제안 실행 완료료
  event ProposalExecuted(uint256 indexed proposalId);

  constructor(
    //이미 배포된 다른 컨트랙트 주소들
    //외부에서 이 컨트랙트를 배포할때, 각각의 컨트랙트 주소를 넘겨줘야 함
    // ex. new Execution(proposalAddress, votingAddress, vaultAddress);
    address _proposal,
    address _voting,
    address payable _vault
  ){
    // 각각의 address를 해당 컨트랙트 타입으로 형변환해서 직접 함수 호출이 가능하도록 만드는 작업
    // ex. proposalContract.getproposalId(id) 처럼 사용가능 
    proposalContract = Proposal(_proposal);
    votingContract = Voting(_voting);
    vaultContract = Vault(_vault);
  }

  // 제안 실행 가능 여부 확인
  function isExecutable(uint256 proposalId) public view returns (bool){
    (uint256 forVotes, uint256 againstVotes) = votingContract.getVotes(proposalId);
    uint256 totalVotes = forVotes + againstVotes;
    if(totalVotes==0) return false;

    return (forVotes * 100 / totalVotes) >= quorumPercent;
  }

  function executeProposal(uint256 proposalId) external {
    require(isExecutable(proposalId), "Execution: quorum not reached");

    // 해당 proposalId에 해당하는 proposal의 복사본 p를 만듬듬
    Proposal.ProposalData memory p = proposalContract.getProposal(proposalId);

    require(p.status == Proposal.Status.Pending, "Execution: invalid status");

    //금고 이체
    vaultContract.transfer(p.recipient, p.amount);
    //제안 상태를 Executed로 변경
    proposalContract.setProposalStatus(proposalId, Proposal.Status.Executed);

    emit ProposalExecuted(proposalId);
  }
}

contracts/GovernanceToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title GovernanceToken
 * @dev DAO 거버넌스 토큰 - 멤버십과 투표 가중치 관리
 *      - 토큰 보유량 기반 투표 가중치
 *      - 스테이킹 시스템
 *      - 멤버 등급별 권한 차등화
 */

contract GovernanceToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE");

    // 멤버 등급별 최소 토큰 보유량
    mapping(uint8 => uint256) public memberTiers;

    // 사용자별 스테이킹 정보
    struct StakingInfo {
        uint256 stakedAmount;
        uint256 stakingStartTime;
        uint256 lastRewardTime;
    }

    mapping(address => StakingInfo) public stakingInfo;

    // 스테이킹 관련 상수
    uint256 public constant MIN_STAKE_AMOUNT = 100 * 10**18; // 100 토큰
    uint256 public constant REWARD_RATE = 5; // 5% 연간 보상
    uint256 public constant LOCK_PERIOD = 30 days;

    // 이벤트
    event TokensStaked(address indexed user, uint256 amount);
    event TokensUnstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event MemberTierUpdated(uint8 tier, uint256 minAmount);

    constructor(
        string memory name,
        string memory symbol,
        address admin
    ) ERC20(name, symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MINTER_ROLE, admin);
        _grantRole(GOVERNANCE_ROLE, admin);

        // 멤버 등급별 최소 토큰 설정
        memberTiers[1] = 100 * 10**18;   // Bronze: 100 토큰
        memberTiers[2] = 500 * 10**18;   // Silver: 500 토큰
        memberTiers[3] = 1000 * 10**18;  // Gold: 1000 토큰
        memberTiers[4] = 5000 * 10**18;  // Platinum: 5000 토큰
    }

    // 토큰 민팅 (관리자만)
    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    // 스테이킹
    function stake(uint256 amount) external {
        require(amount >= MIN_STAKE_AMOUNT, "GovernanceToken: insufficient stake amount");
        require(balanceOf(msg.sender) >= amount, "GovernanceToken: insufficient balance");

        StakingInfo storage info = stakingInfo[msg.sender];

        // 기존 보상 계산 및 지급
        if (info.stakedAmount > 0) {
            uint256 rewards = calculateRewards(msg.sender);
            if (rewards > 0) {
                _mint(msg.sender, rewards);
                info.lastRewardTime = block.timestamp;
            }
        }

        _transfer(msg.sender, address(this), amount);
        info.stakedAmount += amount;
        info.stakingStartTime = block.timestamp;

        emit TokensStaked(msg.sender, amount);
    }

    // 언스테이킹
    function unstake(uint256 amount) external {
        StakingInfo storage info = stakingInfo[msg.sender];
        require(info.stakedAmount >= amount, "GovernanceToken: insufficient staked amount");

        // 보상 계산 및 지급
        uint256 rewards = calculateRewards(msg.sender);
        if (rewards > 0) {
            _mint(msg.sender, rewards);
            info.lastRewardTime = block.timestamp;
        }

        info.stakedAmount -= amount;
        _transfer(address(this), msg.sender, amount);

        emit TokensUnstaked(msg.sender, amount);
    }

    // 보상 청구
    function claimRewards() external {
        uint256 rewards = calculateRewards(msg.sender);
        require(rewards > 0, "GovernanceToken: no rewards to claim");

        StakingInfo storage info = stakingInfo[msg.sender];
        info.lastRewardTime = block.timestamp;

        _mint(msg.sender, rewards);
        emit RewardsClaimed(msg.sender, rewards);
    }

    // 보상 계산
    function calculateRewards(address user) public view returns (uint256) {
        StakingInfo storage info = stakingInfo[user];
        if (info.stakedAmount == 0) return 0;

        uint256 timeElapsed = block.timestamp - info.lastRewardTime;
        return (info.stakedAmount * REWARD_RATE * timeElapsed) / (365 days * 100);
    }

    // 투표 가중치 계산 (보유 토큰 + 스테이킹)
    function getVotingPower(address user) external view returns (uint256) {
        uint256 balance = balanceOf(user);
        uint256 staked = stakingInfo[user].stakedAmount;

        // 스테이킹된 토큰은 1.5배 가중치
        return balance + (staked * 15 / 10);
    }

    // 멤버 등급 확인
    function getMemberTier(address user) external view returns (uint8) {
        uint256 votingPower = this.getVotingPower(user);

        if (votingPower >= memberTiers[4]) return 4; // Platinum
        if (votingPower >= memberTiers[3]) return 3; // Gold
        if (votingPower >= memberTiers[2]) return 2; // Silver
        if (votingPower >= memberTiers[1]) return 1; // Bronze
        return 0; // No tier
    }

    // 멤버 등급별 최소 토큰 설정 (관리자만)
    function setMemberTier(uint8 tier, uint256 minAmount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        memberTiers[tier] = minAmount;
        emit MemberTierUpdated(tier, minAmount);
    }



    // 스테이킹된 토큰 잔액 조회
    function stakedBalanceOf(address user) external view returns (uint256) {
        return stakingInfo[user].stakedAmount;
    }

    // 스테이킹 시작 시간 조회
    function getStakingStartTime(address user) external view returns (uint256) {
        return stakingInfo[user].stakingStartTime;
    }
}

contracts/Proposal.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title Proposal
 * @dev 제안의 등록과 상태 관리를 담당하는 컨트랙트
 *      - 제안 생성(createProposal)
 *      - 단일/전체 제안 조회(getProposal, getAllProposals)
 *      - 상태 변경(on-chain admin only)
 */

// proposal 컨트랙트는 AccessControl 컨트랙트 상속 (역할 생성, 부여 등등 정의)
contract Proposal is AccessControl{
  //DAO "구성원 역할"을 나타내기 위한 고유 ID 정의
  bytes32 public constant MEMBER_ROLE = keccak256("MEMBER_ROLE");

  // enum: 열거형 타입 / status : 제안의 상태를 나타냄
  // pending: 투표가 아직 진행중 / passed, rejected: 통과/기각된 제안 / Executed: 실제 실행완료된 제안
  enum Status { Pending, Passed, Rejected, Executed }

  //제안의 구체적인 정보를 담은 데이터 구조체
  struct ProposalData {
    string title;              // 제안 제목
    string description;        // 상세 설명
    uint256 amount;            // 요청 금액 (wei)
    address payable recipient; // 자금을 받는 사람
    Status status;             // 제안 현재 상태 (pending, passed...)
    uint256 votesFor;          // 찬성표 수
    uint256 votesAgainst;      // 반대표 수
    uint256 startTime;         // 제안 생성 시각 (block.timestamp)
  }

  event ProposalCreated(uint256 indexed id, address indexed proposer);
  event ProposalStatusChanged(uint256 indexed id, Status status);
  // 제안을 저장하는 배열 ( 배열 이름은 proposals이라고 정의)
  // + proposals(0) -> 첫번째 제안 반환 / proposals.length -> 총 제안 개수 조회 가능
  ProposalData[] public proposals;

  constructor(address admin){
    _grantRole(DEFAULT_ADMIN_ROLE, admin); // admin에게 관리자 권안 부여
    _grantRole(MEMBER_ROLE, admin); // admin에게 구성원 역할 부여
  }

  //DAO 구성원이 새로운 제안(Proposal)을 등록할 수 있는 함수수
  function createProposal( // string calldata: 외부 입력으로 받고 가스 절약
        string calldata title,
        string calldata description,
        uint256 amount, // 제안이 요청하는 Ether 양 (단위:Wei)
        address payable recipient //Ether을 수령할 대상 주소 (출금이므로 payable 필요)
    ) external onlyRole(MEMBER_ROLE) returns (uint256) { // DAO 멤버만 제안을 호출할 수 있고 uint256형태로 반환
        // ProposalData 구조체 초기화 후 배열에 추가
        proposals.push(
            ProposalData({
                title: title,
                description: description,
                amount: amount, // 금전 제안인 경우에만 해당 (ex. 디자인 작업한 사람에게 2ETH 보상하자자)
                recipient: recipient,
                status: Status.Pending, // 제안의 초기 상태는 pending
                votesFor: 0, // 초기엔 투표한 사람 없음 -> 찬성0, 반대0
                votesAgainst: 0,
                startTime: block.timestamp // 제안이 생성된 시각각
            })
        );
        // 새로 추가된 제안 ID는 배열의 마지막 인덱스 -> length-1로 게산산
        uint256 pid = proposals.length - 1;
        // 블록체인 로그를 생성하는 부분 (msg.sender는 이 함수를 호출한 계정(=제안자))
        emit ProposalCreated(pid, msg.sender);
        // 새로 생성된 제안의 ID(배열 인덱스) 반환,,, 외부에서 사용 가능
        return pid;
    }

    //단일 제안 정보 조회
    //제안id를 받아서 ProposalData 구조체를 메모리에 복사하여 반환
    //view:상태변경 없이 블록체인 데이터를 읽기 전용으로 조회
    function getProposal(uint256 id) external view returns (ProposalData memory){
      return proposals[id];
    }

    function getAllProposals() external view returns (ProposalData[] memory){
      return proposals;
    }

    // DAO의 제안의 상태를 변경하는 관리자 전용 함수 (통과or기각or 실행되었는지 등등...)
    function setProposalStatus(uint256 id, Status status)
      external
      onlyRole(DEFAULT_ADMIN_ROLE)
    {
      proposals[id].status = status;
      // 이벤트 발생, 프론트엔드나 다른 시스템이 제안 상태 변경을 실시간 감지할 수 있도록 로그를 남김김
      emit ProposalStatusChanged(id, status);
    }

    // * 추가적인 아이디어 *
    // 투표 결과에 따라 자동으로 Passed/rejected 결정
    // 자금 송금이 완료되면 executed로 자동 변경
    // startTime이후 일정 시간만 수정 가능 등 제한 추가
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
* @title Vault
* @dev DAO의 금고 역할을 수행하는 컨트랙트
*      - ETH 입금 받기 (receive)
*      - 잔액 조회 (getBalance)
*      - 권한 있는 역할(TREASURER_ROLE)만 출금 가능
*/

// Valut는 AccessControl이라는 기존 컨트랙트를 상속받음
// AccessControl은 OpenZeppelin 라이브러리에서 제공하는 컨트랙트, 역할 생성/부여/확인/접근 제어 가능
// **** 회계 담당자(Vault) 역할 정의 ****
contract Vault is AccessControl {

  // TREASURE ROLE 이라는 역할을 가진 사람만 실행 가능한 함수.. 의 권한 검사를 할 때 사용될 고유ID
  // 즉, 당신은 TREASURE_ROLE 역할을 수행할 수 있습니까? 를 찾을 수 있는 ID

  // bytes32: 32바이트 고정 길이 데이터타입
  // public : 외부에서 읽을 수 있음 (ex. Valut.TREASURE_ROLE())
  // constant : 상수 선언, 한번 설정되면 변경 불가능한 불변 변수
  // keccak256 : TREASURE_ROLE이라는 문자열을 keccak256이라는 해시함수로 해싱 -> 결과 : bytes32 형식
  // + 이더리움에서 가장 많이 사용되는 암호학적 해시 함수, 고유한 32바이트 해시를 만들어서 역할 식별시 사용
  bytes32 public constant TREASURER_ROLE = keccak256("TREASURER_ROLE");

  //constructor : solidity에서 컨트랙트가 처음 배포될 때 딱 한번만 실행되는 특별한 함수
  constructor(address admin){ // address admin: 배포할때 전달받는 주소형 인자, 이 주소에게 관리자 권한 부여

    // _setupRole : AccessControl 컨트랙트에서 제공하는 내부 함수
    // DEFAULT_ADMIN_ROLE : 모든 역할을 관리할 수 있는 최상위 권한
    _grantRole(DEFAULT_ADMIN_ROLE, admin); // 최고 관리자 권한 부여
    _grantRole(TREASURER_ROLE, admin); // 회계 담당자 역할 권한 부여
  }

  // receive : Ether를 받기 위한 특별한 함수
  // external : 이 함수는 외부에서 호출만 가능
  // payable : 이더를 "받을 수 있는" 함수
  receive() external payable{
    // 의도적으로 빈 함수.. Ether만 받고 아무것도 수행 X
    // 컨트랙트로 Ether을 직접 전송할 때, 데이터 없이 이더만 전송할 때 실행됨!
  }

  // 이 컨트랙트에 얼마나 많은 Ether가 있는지 조회하는 함수
  // ex. uint balance = Valut.getBalance() 를 통해 보유 이더량을 조회 가능
  // getBalance라는 함수, 매개변수x, external(컨트랙트 외부에서만 호출 가능, 내부에서 호출하려면 this.getBalance처럼 사용
  // view : 읽기 전용 함수(가스 소모x) / returns (uint256): 256비트 부호 없는정수를 리턴함
  // why? 이더리움에서 Ether 잔액은 항상 uint256 wei 단위로 표현됨
  function getBalance() external view returns (uint256){
    return address(this).balance;
    //현재 컨트랙트(this)의 주소를 가져와서 이더 잔액을 반환
  }

  //스마트 컨트랙트에 보관된 Ether을 특정 주소로 송금하는 함수 (TREASURER_ROLE 권한을 가진 사람만 가능)
  // address payable to : 이더를 전송할 대상 주소, payable이 붙어 있어야 .trnasfer() 가능
  // uint256 amount : 전송할 이더 금액 (단위 : wei)
  // TREASURER_ROLE 권한을 가진 계정만 호출 가능 + 그렇지 않을시 해당 트랜잭션은 revert(철회?)됨됨
  function transfer(address payable to, uint256 amount)
    external
    onlyRole(TREASURER_ROLE)
  {
    // 잔액 확인
    // require : 조건 검증 구문, 조건이 false일시 트랜잭션 실패 + "vault: insufficient balance" 메시지 출력
    require(address(this).balance >= amount,
            "Vault: insufficient balance");
    // 실제 전송
    to.transfer(amount); //실제 전송
    // .transfer()은 2300gas만 전달되는 안전한 전송 방식임 (실패시 자동 revert)

    // 추가적인 아이디어
    // grantRole(TREASURER_ROLE,) 를 통해 DAO 투표로 회계 담당자 교체
    // emit TransferLog(...) 로 출금 로그 남기기기
  }
}

contracts/Voting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./GovernanceToken.sol";

/**
 * @title Voting
 * @dev 제안에 대한 찬반 투표 로직을 구현하는 컨트랙트
 *      - 투표 시작(startVoting)
 *      - 투표 참여(vote)
 *      - 투표 결과 조회(getVotes, hasVoted)
 */

contract Voting is AccessControl{
  //DAO 구성원 역할
  bytes32 public constant MEMBER_ROLE = keccak256("MEMBER_ROLE");

  // 거버넌스 토큰 컨트랙트
  GovernanceToken public governanceToken;

  constructor(address _governanceToken, address admin){
    governanceToken = GovernanceToken(_governanceToken);
    _grantRole(DEFAULT_ADMIN_ROLE, admin);
    _grantRole(MEMBER_ROLE, admin);
  }

  //단일제안 투표 기록 구조체
  struct VoteRecord{
    mapping(address=>bool) hasVoted; // 중복투표 방지
    //mapping: 일종의 해시테이블(key->value), address: 유저의 지갑 주소, bool: 투표여부... json 생각하면 편함
    uint256 votesFor; //찬성표
    uint256 votesAgainst; //반대표
    uint256 endTime; //투표마감시각
  }

  //proposalId -> VoteRecord 매핑
  //즉, 각 제안에 대한 투표 기록을 저장함 ex. voteRecords[0] -> 0번 제안의 투표 상태
  mapping(uint256=>VoteRecord) public voteRecords;
  // 투표기간 기본값 (예시. 3일)
  uint256 public votingDuration = 3 days;

  //proposalId번 제안에 대해 voter가 support(찬성,반대) 투표했다는 사실을 블록체인 로그에 기록하는 이벤트 선언
  //이 이벤트는 나중에 emit하여 사용될 수 있음 ex. emit Voted(1, msg.sender, true);
  //event는 이벤트 구조를 정의(어떤 데이터를 로그로 기록할지 정의)
  //emit은 정의된 이벤트를 실제로 로그에 기록록
  event Voted(uint256 indexed proposalId, address indexed voter, bool support);

  function startVoting(uint256 proposalId) external onlyRole(DEFAULT_ADMIN_ROLE){
    voteRecords[proposalId].endTime = block.timestamp + votingDuration;
  }

  function vote(uint256 proposalId, bool support) external onlyRole(MEMBER_ROLE){
    VoteRecord storage vr = voteRecords[proposalId];
    // storage: 블록체인에 저장 -> 영구 저장 + 원본에 접근 -> 가스비 더 많이 소비
    // memory : 임시 저장 공간(함수 실행중에만) + 원본은 그대로, 복사본에 접근근

    require(block.timestamp<=vr.endTime, "Voting: period ended");
    //마감 확인
    require(!vr.hasVoted[msg.sender], "Voting: already voted");

    // 투표 가중치 계산
    uint256 votingPower = governanceToken.getVotingPower(msg.sender);
    require(votingPower > 0, "Voting: no voting power");

    vr.hasVoted[msg.sender] = true;
    if(support)  vr.votesFor += votingPower;
    else  vr.votesAgainst += votingPower;

    emit Voted(proposalId, msg.sender, support);
  }

  //투표 결과 조회
  function getVotes(uint256 proposalId)
    external
    view
    returns (uint256 forVotes, uint256 againstVotes)
  {
    VoteRecord storage vr = voteRecords[proposalId];
    return (vr.votesFor, vr.votesAgainst);  
  }

  // 특정 사용자가 해당 제안에 투표했느지 여부 조회
  function hasVoted(uint256 proposalId, address voter)
    external
    view
    returns (bool)
  {
    return voteRecords[proposalId].hasVoted[voter];
  }
}

